template.declaration:
hybrid clock goalTime;
bool goal = false;
void new_xy(double vi, double vf, double hi, double hf) 
                        { 
                            x = x + cos((hi + hf)/2)*((vi+vf)/2) * dt
;                             y = y + sin((hi + hf)/2)*((vi+vf)/2) * dt;
xs[id] = x;
ys[id] = y;
v = vf;
h = hf;
if (reached_goal(x,y)) { 
goal = true;
goalTime = 0;
}
                        }

hybrid clock goalTime;
bool goal = false;
void new_xy(double vi, double vf, double hi, double hf) 
                        { 
                            x = x + cos((hi + hf)/2)*((vi+vf)/2) * dt
;                             y = y + sin((hi + hf)/2)*((vi+vf)/2) * dt;
xs[id] = x;
ys[id] = y;
v = vf;
h = hf;
if (reached_goal(x,y)) { 
goal = true;
goalTime = 0;
}
                        }

bool is_collide = false;
void update_dist()
{
is_collide = false;
min_dist = 99999.0;
for (i = 0; i < num_players; i++){
for (j=i+1; j < num_players; j++) {
min_dist = fmin(hypot(xs[i]-xs[j], ys[i]-ys[j]), min_dist);
if (fint(min_dist) <= 2)
{
is_collide = true;
}
}
}
}
bool on_track = true;
bool works = false;
void is_on_track() {
on_track = true;
for (l = 0; l < num_players; l++){
works = false;
for (m = 0; m < track_points; m++) {
works |= (fint(hypot(xs[l]-center_linex[m], ys[l]-center_liney[m])) <= fint(track_width));
if (works == true) {
m = track_points + 1;
}
}
on_track &= works;
if (on_track == false) {
l = num_players + 1;
}
}
}
